import { XFlow } from './components';
import { XFlowCanvas } from './components/canvas';
/** 通过context获取app */
import { XFlowAppProvider, XFlowAppContext, useXFlowApp } from './components/app-context';
/** 通过context 注册Extension模块 */
import { useExtensionRegistry, ExtensionRegistryContext } from './components/extension-context';
/** XFlowAppExtension */
import { XFlowAppExtensionMoudle } from './components/app-extension-module';
/** classname  prefix  context */
import { useXflowPrefixCls, XFlowConfigProviderContext } from './components/global-config-context';
export { 
// 组件
XFlow, XFlowCanvas, XFlowAppExtensionMoudle, 
// app context
XFlowAppProvider, XFlowAppContext, useXFlowApp, 
// config provider context：使用全局Config
XFlowConfigProviderContext, useXflowPrefixCls, 
// extension context: 注册扩展
ExtensionRegistryContext, useExtensionRegistry, };
/** Graph 配置 */
export { IGraphProvider, createGraphConfig } from './graph';
/** Application 扩展依赖 */
export { FrontendApplication as IApplication, IFrontendApplicationContribution as IApplicationContribution, } from './application';
