import { useState, useEffect } from 'react';
export default function useAsync(fn) {
    const [state, set] = useState({
        loading: true,
    });
    const callback = () => {
        fn().then(value => {
            set({
                loading: false,
                data: value,
            });
        }, error => {
            set({
                loading: false,
            });
            console.error(error);
        });
    };
    useEffect(() => {
        callback();
    }, [callback, fn]);
    return state;
}
