import React, { useEffect } from 'react';

/* Components */
import CallsTableMin from '../CallsTableMin';
import CallsTableNih from '../CallsTableNih';
import CallsTablePfizer from '../CallsTablePfizer';
import CallsTableUkri from '../CallsTableUkri';

/* UI Library Components */
import { Col, Divider, Row, Typography } from 'antd';

/* logotypes */
import logo_sds_color from '../../media/logo_sds_color.svg';
import grants_logo from '../../media/grants-logo.png';
import logo_minciencias from '../../media/logo-minciencias.png';
import pfizer from '../../media/pfizer.svg';
import SDS from '../../media/convo2023.jpg';
import ukri from '../../media/UKRI.svg';

/* UI library Sub-components */
const { Title } = Typography;

const Calls = ({ core }) => {
  useEffect(() => {
    document.title = 'Convocatorias | SALUDATA';
  }, []);

  return (
    <Row justify="center" style={{ marginTop: '50px' }}>
      <Col xxl={20} md={24}>
        <Title className="bold" level={1}>
          Directorio de convocatorias Públicas
        </Title>
        <Title level={5} style={{ color: '#515152' }}>
          Consulte aquí las convocatorias locales, nacionales e internacionales
          que financian o apoyan proyectos de investigación en salud.
        </Title>
        <Divider />
        <Title className="bold" level={3}>
          Secretaría Distrital de Salud
        </Title>
        <img
          className="calls__logo-container"
          src={logo_sds_color}
          alt="Logotipo Secretaría de Salud de Bogotá"
        />
<blockquote class="twitter-tweet"><p lang="es" dir="ltr">📣 
<a href="https://twitter.com/hashtag/CienciaEInnovaci%C3%B3nEnSalud?src=hash&amp;ref_src=twsrc%5Etfw">
  #CienciaEInnovaciónEnSalud</a> | 
  Si haces parte de un grupo de investigación en Bogotá, te invitamos a participar en la convocatoria &#39;
  Cooperación en la Red Pública Distrital de Investigación e Innovación en Salud&#39;, en la que financiaremos proyectos elegidos 
  <a href="https://t.co/hx0mSOXOyH">https://t.co/hx0mSOXOyH</a> 
  <a href="https://t.co/7u1ySd6vCM">pic.twitter.com/7u1ySd6vCM</a>
  </p>&mdash; Secretaría Distrital de Salud (@SectorSalud) 
  <a href="https://twitter.com/SectorSalud/status/1617883669183565825?ref_src=twsrc%5Etfw">
    January 24, 2023</a></blockquote> <script async src="https://platform.twitter.com/widgets.js" charset="utf-8"></script> 
        <Divider />
        <Title className="bold" level={3}>
          NIH Grants & Funding
        </Title>
        <img
          className="calls__logo-container"
          src={grants_logo}
          alt="Logotipo Grants & Funding"
        />
        <CallsTableNih />
        <Divider />
        <Title className="bold" level={3}>
          Pfizer
        </Title>
        <img
          className="calls__logo-container"
          src={pfizer}
          alt="Logotipo Pfizer"
        />
        <CallsTablePfizer />
        <Divider />
        <Title className="bold" level={3}>
          UK Research and Innovation - UKRI
        </Title>
        <img
          className="calls__logo-container"
          src={ukri}
          alt="Logotipo UK Research and Innovation"
        />
        <CallsTableUkri />
        <Divider />
        <Title className="bold" level={3}>
          Minciencias
        </Title>
        <img
          className="calls__logo-container"
          src={logo_minciencias}
          alt="Logotipo Minciencias"
        />
        <CallsTableMin />
        <Divider />
      </Col>
    </Row>
  );
};

export default Calls;
