import React from 'react';
import { Form } from 'antd';
import { ControlSchema } from '../../core';
import { renderFormItemExtra } from '../../core/common/tooltip';
import { FormItemWrapper } from '../../core/common/form-item-wrapper';
import { InputNumber as AInputNumber } from './input-number';

interface Props {
  controlSchema: ControlSchema;
}

export const InputNumber: React.FC<Props> = props => {
  const { controlSchema } = props;
  const { required, tooltip, extra, name, label, placeholder } = controlSchema;

  return (
    <FormItemWrapper schema={controlSchema}>
      {({ disabled, hidden, initialValue }) => {
        return (
          <Form.Item
            name={name}
            label={label}
            initialValue={initialValue}
            tooltip={tooltip}
            extra={renderFormItemExtra(extra)}
            required={required}
            hidden={hidden}
          >
            <AInputNumber placeholder={placeholder} disabled={disabled} />
          </Form.Item>
        );
      }}
    </FormItemWrapper>
  );
};
